import { Component, OnInit } from '@angular/core';
import { Router, NavigationEnd } from '@angular/router';
import {Deliveries} from "../shared/services/Deliveries";

@Component({
  selector: 'app-pages',
  templateUrl: './pages.component.html',
  styleUrls: ['./pages.component.scss'],
  providers: [Deliveries] // add the provider here

})
export class PagesComponent implements OnInit {

  public url : any; 

  constructor(private router: Router) {  
    this.router.events.subscribe((event) => {
          if (event instanceof NavigationEnd) {
            this.url = event.url;
          }
    });
  }

  ngOnInit(): void {
  }


}
<app-header-one [sticky]="true" *ngIf="url != '/pages/comingsoon'"></app-header-one>
<router-outlet></router-outlet>
<script>
    const hideButton = document.getElementById('hide-button');
    const pageContent = document.getElementById('page-content');

    hideButton.addEventListener('click', function() {
        pageContent.style.display = 'none';
    });


</script>

<app-footer-one *ngIf="url != '/pages/comingsoon'"></app-footer-one>
