import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.scss']
})
export class CheckoutComponent implements OnInit {

  constructor() {}

  ngOnInit() {

}}
<div class="form-group col-md-12 col-sm-12 col-xs-12">
  <div class="field-label">Use current location</div>
  <input type="checkbox" id="use-current-location">
  <label for="use-current-location">Use my current location</label>
</div>

<div class="form-group col-md-12 col-sm-12 col-xs-12" id="address-fields">
  <div class="field-label">Country</div>
  <select>
    <option>India</option>
    <option>South Africa</option>
    <option>United States</option>
    <option>Australia</option>
  </select>

  <div class="form-group col-md-12 col-sm-12 col-xs-12">
    <div class="field-label">Address</div>
    <input type="text" name="field-name" value="" placeholder="Street address">
  </div>

  <div class="form-group col-md-12 col-sm-12 col-xs-12">
    <div class="field-label">Town/City</div>
    <input type="text" name="field-name" value="" placeholder="">
  </div>

  <div class="form-group col-md-12 col-sm-6 col-xs-12">
    <div class="field-label">State / County</div>
    <input type="text" name="field-name" value="" placeholder="">
  </div>

  <div class="form-group col-md-12 col-sm-6 col-xs-12">
    <div class="field-label">Postal Code</div>
    <input type="text" name="field-name" value="" placeholder="">
  </div>
</div>

<script>
  const addressFields = document.getElementById("address-fields");
  const useCurrentLocationCheckbox = document.getElementById("use-current-location");

  useCurrentLocationCheckbox.addEventListener("change", (event) => {
    if (event.target.checked) {
      addressFields.style.display = "none";
    } else {
      addressFields.style.display = "";
    }
  });
</script>
