import {Injectable, Provider} from '@angular/core';
import {HttpClient} from '@angular/common/http';
import {Deliveries} from "./Deliveries";
@Injectable({
    providedIn: 'root'
})
export class GetCurrentLocation {
    readonly ASSIGN_LOCATION = 'http://localhost:8083/kaddem/provider/setLatLng/';

    readonly GET_CURRENT_LOCATION = 'http://localhost:8083/kaddem/api/maps';
    constructor(private httpClient: HttpClient) {
    }
    lat: number;
    lng: number;
    delivery: Deliveries;
    getCurrentLocation() {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition((position) => {
               this.lat = position.coords.latitude;
               this.lng = position.coords.longitude;
               console.log(`Latitude: ${this.lat}, Longitude: ${this.lng}`);
            });
            this.delivery.clientLatitude = this.lat;
            this.delivery.clientLongitude = this.lng;

        } else {
            console.log('Geolocation is not supported by this browser.');
        }
    }
    setLatLngToProvider(id){
        return this.httpClient.get<Provider>(this.ASSIGN_LOCATION + id);
    }


}
