import { Injectable } from '@angular/core';
import {Observable} from 'rxjs';
import {HttpClient} from '@angular/common/http';
import { Position } from '@types/web-api';


@Injectable({
    providedIn: 'root'
})
export class GetCurrentLocation {

    readonly GET_CURRENT_LOCATION = 'http://localhost:8083/kaddem/api/maps';
    constructor(private httpClient: HttpClient) {
    }

    getCurrentLocation() {
        navigator.geolocation.getCurrentPosition((response: Position) => {
            const lat = response.coords.latitude;
            const lng = response.coords.longitude;
            console.log(`Latitude: ${lat}, Longitude: ${lng}`);
        });

    }

}
