import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.scss']
})
export class CheckoutComponent implements OnInit {

  constructor() {}

  ngOnInit() {

}}
<script>
  var checkbox = document.querySelector('#useCurrentLocation');
  var addressFields = document.querySelector('#addressFields');

  checkbox.addEventListener('change', function() {
    if (this.checked) {
      addressFields.style.visibility = 'hidden';
    } else {
      addressFields.style.visibility = 'visible';
    }
  });

</script>

<div class="form-group col-md-12 col-sm-12 col-xs-12">
  <div class="field-label">
    <input type="checkbox" id="use-current-location" name="use-current-location" value="true">
    Use current location
  </div>
</div>

<div id="address-fields">
  <div class="form-group col-md-12 col-sm-12 col-xs-12">
    <div class="field-label">Country</div>
    <select>
      <option>India</option>
      <option>South Africa</option>
      <option>United State</option>
      <option>Australia</option>
    </select>
  </div>
  <div class="form-group col-md-12 col-sm-12 col-xs-12">
    <div class="field-label">Address</div>
    <input type="text" name="field-name" value="" placeholder="Street address">
  </div>
  <div class="form-group col-md-12 col-sm-12 col-xs-12">
    <div class="field-label">Town/City</div>
    <input type="text" name="field-name" value="" placeholder="">
  </div>
  <div class="form-group col-md-12 col-sm-6 col-xs-12">
    <div class="field-label">State / County</div>
    <input type="text" name="field-name" value="" placeholder="">
  </div>
  <div class="form-group col-md-12 col-sm-6 col-xs-12">
    <div class="field-label">Postal Code</div>
    <input type="text" name="field-name" value="" placeholder="">
  </div>
</div>
